mapper.update:
UPDATE post
SET title = #{title}, content = #{content}
WHERE id = #{id}

mapper.select:
SELECT p.*, u.name user_name
FROM post p INNER JOIN user u
ON p.user_id = u.id
WHERE p.board_id = #{board_id}
ORDER BY p.no DESC
LIMIT #{startList}, #{pageSize}

SELECT count(*) as listCnt
FROM post
WHERE post.board_id = #{id}

mapper.update:
UPDATE post
SET views = views + 1
WHERE id = #{id};
